import React, { useState } from 'react';
import InstagramLayout from '../components/InstagramLayout';
import { useAuth } from '../context/AuthContext';

const SettingsPage = () => {
  const { user, logout } = useAuth();
  const [notifications, setNotifications] = useState(true);
  const [privacy, setPrivacy] = useState('public');
  const [theme, setTheme] = useState('light');

  const settingsGroups = [
    {
      title: 'Account',
      items: [
        { label: 'Edit Profile', action: () => alert('Edit profile coming soon!') },
        { label: 'Change Password', action: () => alert('Change password coming soon!') },
        { label: 'Privacy and Security', action: () => alert('Privacy settings coming soon!') },
        { label: 'Account Status', action: () => alert('Account status coming soon!') }
      ]
    },
    {
      title: 'Preferences',
      items: [
        { 
          label: 'Notifications', 
          toggle: true, 
          value: notifications, 
          onChange: setNotifications 
        },
        { 
          label: 'Privacy', 
          select: true, 
          value: privacy, 
          options: ['public', 'private', 'friends'],
          onChange: setPrivacy 
        },
        { 
          label: 'Theme', 
          select: true, 
          value: theme, 
          options: ['light', 'dark'],
          onChange: setTheme 
        }
      ]
    },
    {
      title: 'Support',
      items: [
        { label: 'Help Center', action: () => alert('Help center coming soon!') },
        { label: 'Report a Problem', action: () => alert('Report problem coming soon!') },
        { label: 'About', action: () => alert('About KinKeep v3.0') }
      ]
    }
  ];

  return (
    <InstagramLayout>
      <div className="max-w-2xl mx-auto pt-8 px-4">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold mb-2">Settings</h1>
          <p className="text-gray-600">Manage your account and app preferences</p>
        </div>

        {/* User Info */}
        <div className="bg-white rounded-lg border border-gray-200 p-6 mb-6">
          <div className="flex items-center space-x-4">
            <div className="w-16 h-16 rounded-full flex items-center justify-center" style={{background: 'linear-gradient(45deg, #f09433 0%, #e6683c 25%, #dc2743 50%, #cc2366 75%, #bc1888 100%)'}}>
              <span className="text-white text-xl font-medium">{user?.name?.charAt(0)}</span>
            </div>
            <div>
              <h3 className="font-semibold text-lg">{user?.name}</h3>
              <p className="text-gray-600">{user?.email}</p>
              {user?.account_type === 'business' && (
                <span className="inline-block bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs font-medium mt-1">
                  Business Account
                </span>
              )}
            </div>
          </div>
        </div>

        {/* Settings Groups */}
        {settingsGroups.map((group, groupIndex) => (
          <div key={groupIndex} className="bg-white rounded-lg border border-gray-200 mb-6">
            <div className="p-4 border-b border-gray-100">
              <h2 className="font-semibold text-lg">{group.title}</h2>
            </div>
            <div className="divide-y divide-gray-100">
              {group.items.map((item, itemIndex) => (
                <div key={itemIndex} className="p-4 flex items-center justify-between hover:bg-gray-50">
                  <span className="font-medium">{item.label}</span>

                  {item.toggle && (
                    <button
                      onClick={() => item.onChange(!item.value)}
                      className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                        item.value ? 'bg-blue-600' : 'bg-gray-200'
                      }`}
                    >
                      <span
                        className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                          item.value ? 'translate-x-6' : 'translate-x-1'
                        }`}
                      />
                    </button>
                  )}

                  {item.select && (
                    <select
                      value={item.value}
                      onChange={(e) => item.onChange(e.target.value)}
                      className="border border-gray-300 rounded px-3 py-1 text-sm"
                    >
                      {item.options.map(option => (
                        <option key={option} value={option}>
                          {option.charAt(0).toUpperCase() + option.slice(1)}
                        </option>
                      ))}
                    </select>
                  )}

                  {item.action && (
                    <button
                      onClick={item.action}
                      className="text-gray-400 hover:text-gray-600"
                    >
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                      </svg>
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        ))}

        {/* Logout Button */}
        <div className="bg-white rounded-lg border border-gray-200 mb-8">
          <button
            onClick={logout}
            className="w-full p-4 text-left font-medium text-red-600 hover:bg-red-50 rounded-lg transition-colors"
          >
            Log Out
          </button>
        </div>

        {/* App Info */}
        <div className="text-center text-gray-500 text-sm mb-8">
          <p>KinKeep v3.0.0</p>
          <p>Family Story Keeper</p>
        </div>
      </div>
    </InstagramLayout>
  );
};

export default SettingsPage;
