import React, { useState, useEffect, useRef } from 'react';
import InstagramLayout from '../components/InstagramLayout';
import { useAuth } from '../context/AuthContext';
import apiService from '../services/api';

const MessageBubble = ({ message, isOwn }) => {
  return (
    <div className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-2`}>
      <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-2xl ${
        isOwn 
          ? 'bg-blue-accent text-white' 
          : 'bg-gray-200 text-gray-900'
      }`}>
        {message.message_type === 'text' && (
          <p className="text-sm">{message.content}</p>
        )}
        {message.message_type === 'image' && (
          <div>
            <img 
              src={message.media_url} 
              alt="Shared content" 
              className="rounded-lg max-w-full h-auto"
            />
            {message.content && <p className="text-sm mt-2">{message.content}</p>}
          </div>
        )}
        {message.message_type === 'video' && (
          <div>
            <video 
              src={message.media_url} 
              controls 
              className="rounded-lg max-w-full h-auto"
            />
            {message.content && <p className="text-sm mt-2">{message.content}</p>}
          </div>
        )}
        {message.message_type === 'audio' && (
          <div className="flex items-center space-x-2">
            <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
              <path d="M12 3v10.55c-.59-.34-1.27-.55-2-.55-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4V7h4V3h-6z"/>
            </svg>
            <audio src={message.media_url} controls className="max-w-full" />
          </div>
        )}
        <p className="text-xs opacity-75 mt-1">
          {new Date(message.created_at).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
        </p>
      </div>
    </div>
  );
};

const MessagesPage = () => {
  const { user } = useAuth();
  const [conversations] = useState([]);
  const [messages] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(false);
  }, []);

  if (loading) {
    return (
      <InstagramLayout>
        <div className="h-screen flex items-center justify-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-500"></div>
        </div>
      </InstagramLayout>
    );
  }

  return (
    <InstagramLayout>
      <div className="h-screen flex">
        {/* Conversations List */}
        <div className="w-full md:w-1/3 border-r border-gray-300 bg-white">
          <div className="p-4 border-b border-gray-300 flex justify-between items-center">
            <h2 className="text-xl font-semibold">Messages</h2>
            <button 
              onClick={() => alert('New conversation feature coming soon!')}
              className="p-2 hover:bg-gray-100 rounded-full"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
            </button>
          </div>

          <div className="p-8 text-center">
            <svg className="w-24 h-24 mx-auto text-gray-300 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
            </svg>
            <h3 className="text-xl font-medium text-gray-900 mb-2">No conversations yet</h3>
            <p className="text-gray-600">Start a conversation with your family members</p>
          </div>
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex items-center justify-center bg-white">
          <div className="text-center">
            <svg className="w-24 h-24 mx-auto text-gray-300 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
            </svg>
            <h3 className="text-xl font-medium text-gray-900 mb-2">Your Messages</h3>
            <p className="text-gray-600">Send private photos and messages to a friend or group.</p>
          </div>
        </div>
      </div>
    </InstagramLayout>
  );
};

export default MessagesPage;
